
/*** CREATE TABLE ***/

CREATE TABLE TB_DS_IRIS (ID INT IDENTITY(1,1), JSON VARCHAR(MAX))

/*** INSERT VALUES ***/


/*** SIMPLE SELECT ***/

SELECT * FROM TB_DS_IRIS

/*** SELECT - OPENJSON ***/

DECLARE @STRING_JSON VARCHAR(150);
SELECT TOP 1 @STRING_JSON = JSON FROM TB_DS_IRIS;
SELECT * FROM OPENJSON(@STRING_JSON)
-- OR
SELECT * FROM OPENJSON('{"sepalLength": 5.1, "sepalWidth": 3.5, "petalLength": 1.4, "petalWidth": 0.2, "species": "setosa"}')

/*** ISJSON  ***/

SELECT ISJSON('{"sepalLength": 5.1, "sepalWidth": 3.5, "petalLength": 1.4, "petalWidth": 0.2, "species": "setosa"}') -- 1 = true
SELECT ISJSON('sepalLength = 5.1, sepalWidth = 3.5, petalLength = 1.4, petalWidth = 0.2, species = "setosa"') -- 0 = false

/*** SELECT - JSON_VALUE ***/

SELECT
	JSON_VALUE(JSON,'$.sepalLength') AS sepalLength,
	JSON_VALUE(JSON,'$.sepalWidth') AS sepalWidth,
	JSON_VALUE(JSON,'$.petalLength') AS petalLength,
	JSON_VALUE(JSON,'$.petalWidth') AS petalWidth,
	JSON_VALUE(JSON,'$.species') AS species
FROM
	TB_DS_IRIS

/*** SELECT - JSON_VALUE + WHERE ***/

SELECT
	JSON_VALUE(JSON,'$.sepalLength') AS sepalLength,
	JSON_VALUE(JSON,'$.sepalWidth') AS sepalWidth,
	JSON_VALUE(JSON,'$.petalLength') AS petalLength,
	JSON_VALUE(JSON,'$.petalWidth') AS petalWidth,
	JSON_VALUE(JSON,'$.species') AS species
FROM
	TB_DS_IRIS
WHERE
	JSON_VALUE(JSON,'$.sepalLength') >= 5.1
	AND JSON_VALUE(JSON,'$.petalWidth') <= 1.4

/*** MODIFY JSON ***/

DECLARE @STRING_JSON VARCHAR(150);
SELECT TOP 1 @STRING_JSON = JSON FROM TB_DS_IRIS;

SELECT
	@STRING_JSON AS 'ORIGINAL',
	JSON_MODIFY(@STRING_JSON, '$.petalLength', 9.9) AS 'MODIFIED';
